import React, { useState } from "react";
import Carousel from "react-spring-3d-carousel";
import { uuid } from "uuidv4";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCaretRight } from "@fortawesome/free-solid-svg-icons";
import { white } from "color-name";

export default function CarouselSpring() {
  const [goToSlide, setGoToSlide] = useState(0);
  const slides = [
    {
      key: uuid(),
      content: (
        <img
          src="https://freecodecampassets.s3.us-east-2.amazonaws.com/Portfolio+Assets+1/apps/localhost_3006_(iPhone+6_7_8)+(1).png"
          alt="2"
        />
      )
    },
    {
      key: uuid(),
      content: (
        <img
          src="https://freecodecampassets.s3.us-east-2.amazonaws.com/Portfolio+Assets+1/apps/localhost_3006_(iPhone+6_7_8)+(2).png"
          alt="3"
        />
      )
    },
    {
      key: uuid(),
      content: (
        <img
          src="https://freecodecampassets.s3.us-east-2.amazonaws.com/Portfolio+Assets+1/apps/croppedMardownDesktop1.png"
          alt="4"
        />
      )
    },
    {
      key: uuid(),
      content: (
        <img
          src="https://freecodecampassets.s3.us-east-2.amazonaws.com/Portfolio+Assets+1/apps/croppedClockDesktop2.png"
          alt="5"
        />
      )
    },
    {
      key: uuid(),
      content: (
        <img
          src="https://freecodecampassets.s3.us-east-2.amazonaws.com/Portfolio+Assets+1/apps/localhost_3007_(iPhone+6_7_8).png"
          alt="1"
        />
      )
    }
  ];
  return (
    <div className="slides">
      <div className="slide-info">
        React
        <br />
        Intro
        <br />
        Code
        <br />
        App
      </div>
      <Carousel
        slides={slides}
        showNavigation="true"
        offsetRadius={2}
        goToSlide={goToSlide}
      />
      <button
        className="next-button"
        onClick={() => {
          setGoToSlide(goToSlide => goToSlide + 1);
          console.log({ goToSlide });
        }}
      >
        <FontAwesomeIcon icon={faCaretRight} />
      </button>
      <button
        className="prev-button"
        onClick={() => {
          setGoToSlide(goToSlide => goToSlide - 1);
          console.log({ goToSlide });
        }}
      >
        <FontAwesomeIcon icon={faCaretLeft} />
      </button>
    </div>
  );
}
