import React from "react";
import sizeMe from "react-sizeme";
import ipadBG2 from "./images/ipadBG2.svg";
import iphoneBG2 from "./images/iphoneBG2.svg";

function Background({ size }) {
  const { width } = size;

  if (width < 1030 && width > 750) {
    return <img className="skyline-image" src={ipadBG2}></img>;
  } else if (width < 750) {
    return <img className="skyline-image" src={iphoneBG2}></img>;
  } else {
    return (
      <img
        className="skyline-image"
        src="https://freecodecampassets.s3.us-east-2.amazonaws.com/Portfolio+Assets+1/palmsSunset1Artboard+1.png"
      ></img>
    );
  }
}

export default sizeMe()(Background);
