import React, { useState } from "react";
import Carousel from "react-spring-3d-carousel";
import { uuid } from "uuidv4";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCaretRight } from "@fortawesome/free-solid-svg-icons";
import { faCaretLeft } from "@fortawesome/free-solid-svg-icons";
import { white } from "color-name";
import testd from "./images/testd.svg";
import markown from "./images/markown.svg";
import computerB from "./images/computerB.svg";
import quotesB from "./images/quotesB.svg";
import clockB from "./images/clockB.svg";
import calculator from "./images/calculator.svg";
import fathack from "./images/fathack.svg";

export default function CarouselSpring({
  nextProperty,
  prevProperty,
  name,
  color,
  appLink,
  gitLink
}) {
  const [goToSlide, setGoToSlide] = useState(0);

  const nextFunc = nextProperty;
  const prevFunc = prevProperty;

  const slides = [
    {
      key: uuid(),
      content: (
        <a href={appLink} className="pcAnchor">
          <img src={testd} alt="2" className="phone" />
        </a>
      )
    },
    {
      key: uuid(),
      content: <img src={quotesB} alt="3" className="phone" />
    },
    {
      key: uuid(),
      content: <img src={markown} alt="4" />
    },
    {
      key: uuid(),
      content: <img src={clockB} alt="5" className="clock" />
    },
    {
      key: uuid(),
      content: <img src={calculator} alt="6" className="phone" />
    },
    {
      key: uuid(),
      content: <img src={fathack} alt="1" className="phone" />
    }
  ];
  return (
    <div className="slides">
      <div className="slide-info">
        <div className="app-title" style={{ color: color }}>
          {name}
        </div>
        <a className="app-link">INTRO + CODING DIARY</a>
        <br />
        <a className="app-link" href={gitLink}>
          VIEW CODE
        </a>
        <br />
        <a className="app-link" href={appLink}>
          VIEW APP
        </a>
      </div>
      <div className="carouselBox">
        <Carousel
          slides={slides}
          showNavigation={false}
          offsetRadius={2}
          goToSlide={goToSlide}
        />{" "}
      </div>
      <div className="carouselBox2">
        <Carousel
          slides={slides}
          showNavigation={false}
          offsetRadius={0}
          goToSlide={goToSlide}
        />{" "}
      </div>

      <button
        className="prev-button"
        onClick={() => {
          setGoToSlide(goToSlide => goToSlide - 1);
          prevFunc();
        }}
      >
        <FontAwesomeIcon
          icon={faCaretLeft}
          style={{ height: "80px", width: "80px" }}
        />
      </button>
      <button
        className="next-button"
        onClick={() => {
          setGoToSlide(goToSlide => goToSlide + 1);
          nextFunc();
          console.log(goToSlide);
        }}
      >
        <FontAwesomeIcon
          icon={faCaretRight}
          style={{ height: "80px", width: "80px" }}
        />
      </button>
    </div>
  );
}
