import React, { useState } from "react";
import Carousel from "react-spring-3d-carousel";
import { uuid } from "uuidv4";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCaretRight } from "@fortawesome/free-solid-svg-icons";
import { faCaretLeft } from "@fortawesome/free-solid-svg-icons";
import { white } from "color-name";
import drumPNG from "./images/drumPNG.png";
import quotesBPNG from "./images/quotesBPNG.png";
import markownPNG from "./images/markownPNG.png";
import clockBPNG from "./images/clockBPNG.png";
import calculatorPNG from "./images/calculatorPNG.png";
import fathackPNG from "./images/fathackPNG.png";

export default function CarouselSpring({
  nextProperty,
  prevProperty,
  name,
  color,
  appLink,
  gitLink
}) {
  const [goToSlide, setGoToSlide] = useState(0);

  const nextFunc = nextProperty;
  const prevFunc = prevProperty;

  const slides = [
    {
      key: uuid(),
      content: <img src={drumPNG} alt="2" className="phone" />
    },
    {
      key: uuid(),
      content: <img src={quotesBPNG} alt="3" className="phone" />
    },
    {
      key: uuid(),
      content: <img src={markownPNG} alt="4" className="phone" />
    },
    {
      key: uuid(),
      content: <img src={clockBPNG} alt="5" className="phone" />
    },
    {
      key: uuid(),
      content: <img src={calculatorPNG} alt="1" className="phone" />
    },
    {
      key: uuid(),
      content: <img src={fathackPNG} alt="1" className="phone" />
    }
  ];
  return (
    <div className="slides">
      <div className="slide-title">
        <div className="app-title" style={{ color: color }}>
          {name}
        </div>
      </div>
      <div className="slide-links">
        <a className="app-link">INTRO + CODING DIARY</a>
        <br />
        <a className="app-link" href={gitLink}>
          VIEW CODE
        </a>
        <br />
        <a className="app-link" href={appLink}>
          VIEW APP
        </a>
      </div>
      <div className="carouselBox">
        <Carousel
          slides={slides}
          showNavigation={false}
          offsetRadius={0}
          goToSlide={goToSlide}
        />{" "}
      </div>
      <div className="carouselBox2">
        <Carousel
          slides={slides}
          showNavigation={false}
          offsetRadius={0}
          goToSlide={goToSlide}
        />{" "}
      </div>
      <div className="button-box">
        <button
          className="prev-button"
          onClick={() => {
            setGoToSlide(goToSlide => goToSlide - 1);
            prevFunc();
          }}
        >
          <FontAwesomeIcon
            icon={faCaretLeft}
            style={{ height: "80px", width: "80px" }}
          />
        </button>
        <button
          className="next-button"
          onClick={() => {
            setGoToSlide(goToSlide => goToSlide + 1);
            nextFunc();
          }}
        >
          <FontAwesomeIcon
            icon={faCaretRight}
            style={{ height: "80px", width: "80px" }}
          />
        </button>
      </div>
    </div>
  );
}
